Rules.InvariantScript:
Set(
    PopupVis,
    false
);
Set(
    varTimeValue,
    Blank()
);
Refresh('TG14 Task Information')

Set(
    varNotifyWindow,
    true
);
Set(
    varCustomerEmail,
    'Approvals Gallery'.Selected.'Customer Email'
);
Set(
    varApprovalscheck,
    true
);
Patch(
    'TG14 Task Information',
    First(
        Filter(
            'TG14 Task Information',
            ID = cmp_ApprovalsPopup.SelectedJob.ID
        )
    ),
    {
        Title: cmp_ApprovalsPopup.SelectedJob.Title,
        'Response ID': cmp_ApprovalsPopup.SelectedJob.ResponseID,
        Status: cmp_ApprovalsPopup.SelectedJob.Status,
        'Task Date': DatePicker1.SelectedDate,
        'Start Time': TextCanvas3.Text,
        'Project Category': cmp_ApprovalsPopup.SelectedJob.'Project Category',
        'Service they Require': cmp_ApprovalsPopup.SelectedJob.'Service Required',
        InternalUpdates: {Value: "Yes"},
        UpdatedByFlow: {Value: "Yes"},
        Rank: cmp_ApprovalsPopup.SelectedJob.Rank,
        'Customer Name': cmp_ApprovalsPopup.SelectedJob.'Customer Name',
        'Customer Section': cmp_ApprovalsPopup.SelectedJob.'Customer Section',
        'Customer Email': cmp_ApprovalsPopup.SelectedJob.'Customer Email',
        'Customer Contact Number': cmp_ApprovalsPopup.SelectedJob.'Customer Contact Number',
        'POC if Different': cmp_ApprovalsPopup.SelectedJob.'POC if Different',
        'LM Rank Name': cmp_ApprovalsPopup.SelectedJob.'LM Rank Name',
        'LM Section': cmp_ApprovalsPopup.SelectedJob.'LM Section',
        'LM Contact Number': cmp_ApprovalsPopup.SelectedJob.'LM Contact Number',
        'LM O365 data': cmp_ApprovalsPopup.SelectedJob.'LM O365 Data',
        'Task Location': cmp_ApprovalsPopup.SelectedJob.'Task Location',
        'Task Requirements': cmp_ApprovalsPopup.SelectedJob.'Task Requirements',
        Deadline: cmp_ApprovalsPopup.SelectedJob.Deadline,
        Hazards: cmp_ApprovalsPopup.SelectedJob.Hazards,
        TLB: cmp_ApprovalsPopup.SelectedJob.TLB,
        'Media/PR Request Cont.': cmp_ApprovalsPopup.SelectedJob.'Media/PR Request Details',
        'Visit Details': cmp_ApprovalsPopup.SelectedJob.'Visit Details',
        'Official Groups': cmp_ApprovalsPopup.SelectedJob.'Official Groups',
        'Portrait Request': cmp_ApprovalsPopup.SelectedJob.'Portrait Request',
        'Image Reference': cmp_ApprovalsPopup.SelectedJob.'Image Reference',
        'No of Copies': cmp_ApprovalsPopup.SelectedJob.'No of Copies',
        'Print Size': cmp_ApprovalsPopup.SelectedJob.'Print Size',
        'Print Finish': cmp_ApprovalsPopup.SelectedJob.'Print Finish',
        Notes: cmp_ApprovalsPopup.SelectedJob.Notes,
        'Tail Number': cmp_ApprovalsPopup.SelectedJob.'Tail Number',
        Unit: {Value: gblUserLocPerm},
        'Approval Status': {Value: "Approved"}
    }
);
Set(
    varCurrentTasksCheck,
    true
);
Set(
    varEmailContent,
    'Approve HTML Text'.HtmlText
);
Office365Outlook.SendEmail(
    varCustomerEmail,
    Concatenate(
        Text(
            Today(),
            "yyyymmdd",
            "en-GB"
        ),
        "-",
        "Your Photographic Tasking ",
        Text(
            Value(cmp_ApprovalsPopup.SelectedJob.Title),
            "0000",
            "en-GB"
        ),
        "-O"
    ),
    varEmailContent,
    {IsHtml: true}
);
Set(
    varCustEmailSent,
    true
);
Set(
    varNotifyWindow,
    false
);
Set(
    PopupVis,
    false
);
Concurrent(
    Set(
        var1177Check,
        false
    ),
    Set(
        varCurrentTasksCheck,
        false
    ),
    Set(
        varCustEmailSent,
        false
));
If(
    cmp_ApprovalsPopup.SelectedJob.Unit.Value = "Lossiemouth",
    Office365Groups.CreateCalendarEvent(
        "4597c298-6aed-436a-b930-c805e9e0e150",
        Concatenate(
            cmp_ApprovalsPopup.SelectedJob.Title,
            "-",
            Concatenate(
                cmp_ApprovalsPopup.SelectedJob.Rank,
                " ",
                'Customer Name'
            ),
            cmp_ApprovalsPopup.SelectedJob.'Project Category'.Value
        ),
        {
            dateTime: Concatenate(
                Text(
                    cmp_ApprovalsPopup.SelectedJob.'Task Date',
                    "yyyy/mm/dd",
                    "en-GB"
                ),
                ", ",
                cmp_ApprovalsPopup.SelectedJob.'Start Time'
            ),
            timeZone: DateTimeZone.Local
        },
        {
            dateTime: Concatenate(
                Text(
                    cmp_ApprovalsPopup.SelectedJob.'Task Date',
                    "yyyy/mm/dd",
                    "en-GB"
                ),
                ", ",
                TimeValue(
                    cmp_ApprovalsPopup.SelectedJob.'Start Time',
                    "en-GB"
                )
            ),
            timeZone: DateTimeZone.Local
        },
        {
            body: {
                content: HtmlText3.HtmlText,
                contentType: "Text"
            },
            isAllDay: false,
            isReminderOn: true,
            reminderMinutesBeforeStart: 30,
            showAs: "Busy",
            responseRequested: false
        }
    )
)

Patch(
    'TG14 Task Information',
    First(
        Filter(
            'TG14 Task Information',
            ID = cmp_ApprovalsPopup.SelectedJob.ID
        )
    ),
    {
        'Approval Status': {Value: "Rejected"},
        Comments: If(
            IsBlank('Reject Comments_1'),
            "Nil",
            'Reject Comments_1'.Value
        ),
        Status: {Value: "CANCELLED"}
    }
);
Set(
    PopupVis,
    false
);
Set(
    RejectPopupVis,
    false
);
Set(
    varEmailContent,
    'Reject HTML Text'.HtmlText
) && Office365Outlook.SendEmailV2(
    'Approvals Gallery'.Selected.'Customer Email',
    Concatenate(
        Text(
            Today(),
            "yyyymmdd",
            "en-GB"
        ),
        "-",
        "Your Photographic Tasking ",
        Text(
            Value('Approvals Gallery'.Selected.Title),
            "0000",
            "en-GB"
        ),
        "-O"
    ),
    varEmailContent
);
If(
    "2022" in 'Add Task Date',
    Patch(
        '1177 Task Register 2022',
        First(
            Filter(
                '1177 Task Register 2022',
                'Response ID' = Text('Approvals Gallery'.Selected.'Response ID')
            )
        ),
        {Status: "CANCELLED"}
    ),
    If(
        "2023" in 'Add Task Date',
        Patch(
            '1177 Task Register 2023',
            First(
                Filter(
                    '1177 Task Register 2023',
                    'Response ID' = Text('Approvals Gallery'.Selected.'Response ID')
                )
            ),
            {Status: "CANCELLED"}
        ),
        If(
            "2024" in 'Add Task Date',
            Patch(
                '1177 Task Register 2024',
                First(
                    Filter(
                        '1177 Task Register 2024',
                        'Response ID' = Text('Approvals Gallery'.Selected.'Response ID')
                    )
                ),
                {Status: "CANCELLED"}
            )
        )
    )
);

Patch(
    'TG14 Task Information',
    First(
        Filter(
            'TG14 Task Information',
            ID = cmp_ApprovalsPopup.SelectedJob.ID
        )
    ),
    {
        'Approval Status': {Value: "Rejected"},
        Comments: If(
            IsBlank('Reject Comments_2'),
            "Nil",
            'Reject Comments_2'.Value
        ),
        Status: {Value: "CANCELLED"}
    }
);
Set(
    PopupVis,
    false
);
Set(
    RejectPopupVis,
    false
);
Set(
    varEmailContent,
    'Reject HTML Text'.HtmlText
) && Office365Outlook.SendEmailV2(
    'Approvals Gallery'.Selected.'Customer Email',
    Concatenate(
        Text(
            Today(),
            "yyyymmdd",
            "en-GB"
        ),
        "-",
        "Your Photographic Tasking ",
        Text(
            Value('Approvals Gallery'.Selected.Title),
            "0000",
            "en-GB"
        ),
        "-O"
    ),
    varEmailContent
);
If(
    "2022" in 'Add Task Date',
    Patch(
        '1177 Task Register 2022',
        First(
            Filter(
                '1177 Task Register 2022',
                'Response ID' = Text('Approvals Gallery'.Selected.'Response ID')
            )
        ),
        {Status: "CANCELLED"}
    ),
    If(
        "2023" in 'Add Task Date',
        Patch(
            '1177 Task Register 2023',
            First(
                Filter(
                    '1177 Task Register 2023',
                    'Response ID' = Text('Approvals Gallery'.Selected.'Response ID')
                )
            ),
            {Status: "CANCELLED"}
        ),
        If(
            "2024" in 'Add Task Date',
            Patch(
                '1177 Task Register 2024',
                First(
                    Filter(
                        '1177 Task Register 2024',
                        'Response ID' = Text('Approvals Gallery'.Selected.'Response ID')
                    )
                ),
                {Status: "CANCELLED"}
            )
        )
    )
);
