Rules.InvariantScript:
Set(
    varViewLoanCard,
    false
);
Concurrent(
    Reset(TextInputCanvas4),
    Reset(TextInputCanvas4_1),
    Reset('Customer Select'),
    Reset(TextInputCanvas4_3),
    Reset(TextInputCanvas4_4),
    Reset(DatePickerCanvas3)
);

Set(
    varLoanNotifyWindow,
    true
);
ForAll(
    LoanCard,
    UpdateIf(
        'Asset Tracker_1',
        'LC Serial Number' = 'Serial number',
        {
            Status: {Value: "Loan"},
            Location: {Value: "In Use"},
            'Previous owner': First(
                Filter(
                    'Asset Tracker_1',
                    'LC Serial Number' = 'Serial number'
                )
            ).'Current owner',
            'Current owner': 'Customer Select'.Selected,
            'Due date': DatePickerCanvas3.Value
        }
    )
);
Patch(
    'TG14 Loan Register',
    Defaults('TG14 Loan Register'),
    {
        'Loan Date & Time': DateTimeValue(
            Now(),
            "en-GB"
        ),
        Recipient: TextInputCanvas4.Value,
        'Section/Role': TextInputCanvas4_1.Value,
        'Contact Email': 'Customer Select'.Selected.Email,
        'Contact Number': TextInputCanvas4_3.Value,
        'Loaned Items': Concat(
            LoanCard,
            Concatenate(
                'LC Asset Tag',
                " / ",
                'LC Asset type',
                " / ",
                'LC Manufacturer'.Value,
                " / ",
                'LC Model',
                " / ",
                'LC Serial Number'
            ),
            "<br>"
        )
    }
);
Set(
    varLoanRegisterCheck,
    true
);
Office365Outlook.SendEmail(
    'Customer Select'.Selected.Email,
    Concatenate(
        Text(
            Now(),
            "yyyymmdd",
            "en-GB"
        ),
        "-Your MPT Equipment Loan-O"
    ),
    "",
    {
        Importance: "High",
        IsHtml: true
    }
);
Set(
    varLoanCustEmailSent,
    true
);
Concurrent(
    Set(
        varLoanNotifyWindow,
        false
    ),
    Set(
        varViewLoanCard,
        false
    ),
    Set(
        varLoanCustEmailSent,
        false
    ),
    Set(
        varLoanRegisterCheck,
        false
    ),
    Reset(DatePickerCanvas3),
    Reset('Customer Select'),
    Clear(LoanCard)
);
