Rules.InvariantScript:
If(
    cmp_TimePicker.Default=Blank()
    And varTimeValue=Blank(),

    // value when no default is supplied
    {Value: "08"},

    // value when default is supplied or hours were clicked
    With(
        {
            wTime: Coalesce(
                varTimeValue,
                cmp_TimePicker.Default
            )
        },
        LookUp(
            Self.AllItems.Value,
            Value = Text(
                Hour(wTime),
                "[$-en-GB]00"
            )
        )
    )
)

With(
    {
        wTime: Coalesce(
            varTimeValue,
            cmp_TimePicker.Default
        )
    },
    LookUp(
        Self.AllItems.Value,
        Value = Text(
            Minute(wTime),
            "[$-en-US]00"
        )
    )
)
